  <style>
    :root{
      --primary:#2236c2;
      --accent:#22b573;
      --muted:#767777;
      --card-bg: #ffffff;
      --page-bg: #f5f7f9;
      --shadow: 0 10px 22px rgba(15,23,42,0.06);
    }

    html,body{
      height:100%;
      
      background: var(--page-bg);
      font-family: 'Poppins', sans-serif;
      color: #111827;
    }

    /* top bar */
    .topbar {
      height:56px;
      background: #222427;
      color: #fff;
      display:flex;
      align-items:center;
      padding:0 18px;
      font-weight:600;
      box-shadow: 0 2px 6px rgba(0,0,0,0.05);
    }

    .dashboard-wrap {
      display: flex;
      gap: 2rem;
      padding: 2% 7%;
      align-items:flex-start;
    }

    /* sidebar */
    .sidebar {
      width: 300px;
      /* height: 280px; */
      background: var(--card-bg);
      border-radius: 14px;
      padding: 18px;
      box-shadow: var(--shadow);
      position: sticky;
      top: 20px;
      align-self:flex-start;
    }

   

    .side-nav {
      margin-top: 12px;
      display:flex;
      flex-direction: column;
      gap: 8px;
    }

    .side-nav a {
      display:flex;
      align-items:center;
      gap:12px;
      padding: 12px 10px;
      border-radius: 12px;
      color:#1f2937;
      text-decoration:none;
      transition: all .18s ease;
      font-weight:500;
    }

    .side-nav a.active {
      background: var(--primary);
      color:#fff;
      
      box-shadow: 0 6px 18px rgba(47, 64, 191, 0.12);
    }
    .side-nav a.active span svg{
      filter: invert(1);
    }
    .side-nav a:hover { transform: translateY(-2px); }

    /* main panel */
    .main-panel {
      flex: 1;
      min-width: 0;
    }

    .card-panel {
      background: var(--card-bg);
      border-radius: 14px;
      padding: 18px;
      box-shadow: var(--shadow);
      margin-bottom: 18px;
    }

    .search-input {
      height:56px;
      border-radius: 10px;
      background: #f3f4f6;
      border: none;
      padding: 12px 16px;
      font-size:15px;
    }

    .filter-row .form-control, .filter-row .form-select {
      height:48px;
      border-radius:10px;
      background: #f3f4f6;
      border: none;
    }

    /* ride card */
    .ride-card {
      display:flex;
      justify-content:space-between;
      align-items:center;
      padding:16px;
      border-radius: 12px;
      background: white;
      box-shadow: 0 8px 20px rgba(0,0,0,0.04);
      margin-bottom:14px;
    }

    .ride-left {
      display:flex;
      gap:12px;
      align-items:center;
    }

    .ride-left img {
      width:56px;
      height:56px;
      border-radius:50%;
      object-fit:cover;
      border: 3px solid #fff;
      box-shadow: 0 6px 14px rgba(0,0,0,0.06);
    }

    .ride-meta small { color:var(--muted); display:block; }

    .price {
      font-weight:700;
      color: var(--primary);
      font-size:1.25rem;
    }

    /* responsive adjustments */
    @media (max-width: 992px) {
      .dashboard-wrap { padding: 18px; gap: 1rem; flex-direction: column; }
      .sidebar { width:100%; display:flex;   justify-content:space-between; align-items:center; padding:12px 12px; position: relative;  margin-bottom: 3%;}
      .side-nav { flex-direction:column; gap:6px; }
      .side-nav a {width:25rem;   padding:10px; font-size:14px; border-radius:10px; }
      .sidebar .logo { display:none; }
      .topbar { font-size:14px; padding: 8px 12px; }
    }

    @media (max-width: 576px) {
      .ride-left img { width:48px; height:48px; }
      .search-input { height:48px; }
    }
  </style>

